mapper.select:
SELECT

FROM wf_dept_head a

WHERE a.id = #{id}

SELECT

FROM wf_dept_head a



	${dataScope}

		AND a.site = #{site}


		AND a.dept LIKE
		    '%'||#{dept}||'%'
			'%'||#{dept}||'%'
			'%'+#{dept}+'%'
			concat('%',#{dept},'%')


		AND a.approver = #{approver.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM wf_dept_head a



	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wf_dept_head SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
