mapper.select:
SELECT

FROM wf_delegate a

WHERE a.id = #{id}

SELECT

FROM wf_delegate a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.owner = #{owner.id}


		AND a.delegate = #{delegate.id}


		AND a.application_no LIKE
		    '%'||#{applicationNo}||'%'
			'%'||#{applicationNo}||'%'
			'%'+#{applicationNo}+'%'
			concat('%',#{applicationNo},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM wf_delegate a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wf_delegate a


	a.del_flag = #{DEL_FLAG_NORMAL}
	AND now() > a.start_time AND now() < end_time

		AND a.owner = #{owner.id}


		AND a.delegate = #{delegate.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wf_delegate a


	a.del_flag = #{DEL_FLAG_NORMAL}

	AND  a.end_time > #{startTime} AND a.start_time < #{endTime}  


		AND a.id != #{id}


		AND a.owner = #{owner.id}



		AND a.proc_ins_id = #{procInsId}


		AND a.task_id = #{taskId}





		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wf_delegate SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
